function getSummaryImproved(e, r) {
    var t = /<.*?>/gi,
        a = /<br.*?>/gi,
        s = /(<\/{1}p>)|(<\/{1}div>)/gi,
        i = /(<style.*?\/{1}style>)|(<script.*?\/{1}script>)|(<table.*?\/{1}table>)|(<form.*?\/{1}form>)|(<code.*?\/{1}code>)|(<pre.*?\/{1}pre>)/gi;
