        fade: function($cont, $slides, opts) {
            $slides["not"](":eq(" + opts["currSlide"] + ")")["css"]("opacity", 0);
            opts["before"]["push"](function(curr, next, opts) {
                $["fn"]["cycle"]["commonReset"](curr, next, opts);
                opts["cssBefore"]["opacity"] = 0
            });
            opts["animIn"] = {
                opacity: 1
            };
            opts["animOut"] = {
                opacity: 0
            };
            opts["cssBefore"] = {
                top: 0,
                left: 0
            }
        }
    };
