//Slider Script
var snumposts = 5;
var sdisplaymore = true;
var sshowpostsummary = true;
var snumchars = 150;
        
function showfeaturepostswiththumbs(json) {
  document.write('<ul class="rslides">');
  for(var i = 0; i < snumposts; i++) {
    var entry = json.feed.entry[i];
    var posttitle = entry.title.$t;
    var posturl;
    if(i == json.feed.entry.length) break;
    for(var k = 0; k < entry.link.length; k++) {
      if(entry.link[k].rel == 'alternate') {
        posturl = entry.link[k].href;
        break
      }
    }
    var thumburl;
    try {
      thumburl = entry.media$thumbnail.url
    } catch(error) {
      s = entry.content.$t;
      a = s.indexOf("<img");
      b = s.indexOf("src=\"", a);
      c = s.indexOf("\"", b + 5);
      d = s.substr(b + 5, c - b - 5);
      if((a != -1) && (b != -1) && (c != -1) && (d != "")) {
        thumburl = d
      } else thumburl = 'https://lh3.googleusercontent.com/_FQCCkMjyvSA/TWoCU9DxctI/AAAAAAAAAHk/ZW-sgbYYWBs/thumb_missing.jpg'
    }
    document.write('<li>');
    document.write('<a class="feature_thumb_container" href="' + posturl + '"><img class="feature_thumb" src="' + thumburl.replace('/s72-c/', '/s1600/') +
      '"/><div class="thumb-hover"></div></a>');
    document.write('<div class="slide-caption"><div class="slide-caption-wrap">');
    document.write('<a class="post-title" href="' + posturl + '" target ="_top">' + posttitle + '</a>');
    if("content" in entry) {
      var postcontent = entry.content.$t
    } else if("summary" in entry) {
      var postcontent = entry.summary.$t
    } else var postcontent = "";
    var re = /<\S[^>]*>/g;
    postcontent = postcontent.replace(re, "");
    if(sshowpostsummary == true) {
      if(postcontent.length < snumchars) {
        document.write('<span>');
        document.write(postcontent);
        document.write('</span>')
      } else {
        document.write('<span>');
        postcontent = postcontent.substring(0, snumchars);
        var quoteEnd = postcontent.lastIndexOf(" ");
        postcontent = postcontent.substring(0, quoteEnd);
        document.write(postcontent + '...');
        document.write('</span>')
      }
    }
    var towrite = '';
    var flag = 0;
    document.write('<strong class="read-more-feat">');
    if(sdisplaymore == true) {
      if(flag == 1) towrite = towrite + ' Â· ';
      towrite = towrite + '<a href="' + posturl + '" class="url" target ="_top">Continue Reading</a>';
      flag = 1
    }
    document.write(towrite);
    document.write('</strong></div></div>');
    document.write('</li>')
  }
  document.write('</ul>')
}

