                maxh = h > maxh ? h : maxh
            };
            if (lI1l > 0 && maxh > 0) {
                $cont["css"]({
                    width: lI1l + "px",
                    height: maxh + "px"
                })
            }
        };
        if (opts["pause"]) {
            $cont["hover"](function() {
                this["cyclePause"]++
            }, function() {
                this["cyclePause"]--
            })
        };
        if (supportMultiTransitions(opts) === false) {
            return false
        };
        var lIIl = false;
        options["requeueAttempts"] = options["requeueAttempts"] || 0;
        $slides["each"](function() {
            var lII1 = $(this);
            this["cycleH"] = (opts["fit"] && opts["height"]) ? opts["height"] : (lII1["height"]() || this["offsetHeight"] || this["height"] || lII1["attr"]("height") || 0);
            this["cycleW"] = (opts["fit"] && opts["width"]) ? opts["width"] : (lII1["width"]() || this["offsetWidth"] || this["width"] || lII1["attr"]("width") || 0);
            if (lII1["is"]("img")) {
                var lIII = ($["browser"]["msie"] && this["cycleW"] == 28 && this["cycleH"] == 30 && !this["complete"]);
                var l1lll = ($["browser"]["mozilla"] && this["cycleW"] == 34 && this["cycleH"] == 19 && !this["complete"]);
                var l1ll1 = ($["browser"]["opera"] && ((this["cycleW"] == 42 && this["cycleH"] == 19) || (this["cycleW"] == 37 && this["cycleH"] == 17)) && !this["complete"]);
                var l1llI = (this["cycleH"] == 0 && this["cycleW"] == 0 && !this["complete"]);
                if (lIII || l1lll || l1ll1 || l1llI) {
                    if (o["s"] && opts["requeueOnImageNotLoaded"] && ++options["requeueAttempts"] < 100) {
                        log(options["requeueAttempts"], " - img slide not loaded, requeuing slideshow: ", this["src"], this["cycleW"], this["cycleH"]);
                        setTimeout(function() {
                            $(o["s"], o["c"])["cycle"](options)
                        }, opts["requeueTimeout"]);
                        lIIl = true;
                        return false
                    } else {
                        log("could not determine size of image: " + this["src"], this["cycleW"], this["cycleH"])
                    }
                }
            };
            return true
        });
        if (lIIl) {
            return false
        };
        opts["cssBefore"] = opts["cssBefore"] || {};
        opts["animIn"] = opts["animIn"] || {};
        opts["animOut"] = opts["animOut"] || {};
        $slides["not"](":eq(" + lIl1 + ")")["css"](opts["cssBefore"]);
        if (opts["cssFirst"]) {
            $($slides[lIl1])["css"](opts["cssFirst"])
        };
        if (opts["timeout"]) {
            opts["timeout"] = parseInt(opts["timeout"]);
            if (opts["speed"]["constructor"] == String) {
                opts["speed"] = $["fx"]["speeds"][opts["speed"]] || parseInt(opts["speed"])
            };
            if (!opts["sync"]) {
                opts["speed"] = opts["speed"] / 2;
            };
            var l1l1l = opts["fx"] == "shuffle" ? 500 : 250;
            while ((opts["timeout"] - opts["speed"]) < l1l1l) {
                opts["timeout"] += opts["speed"];
            };
        };
        if (opts["easing"]) {
            opts["easeIn"] = opts["easeOut"] = opts["easing"];
        };
        if (!opts["speedIn"]) {
            opts["speedIn"] = opts["speed"];
        };
        if (!opts["speedOut"]) {
            opts["speedOut"] = opts["speed"];
        };
        opts["slideCount"] = els["length"];
        opts["currSlide"] = opts["lastSlide"] = lIl1;
        if (opts["random"]) {
            if (++opts["randomIndex"] == els["length"]) {
                opts["randomIndex"] = 0;
            };
            opts["nextSlide"] = opts["randomMap"][opts["randomIndex"]];
        } else {
            if (opts["backwards"]) {
                opts["nextSlide"] = opts["startingSlide"] == 0 ? (els["length"] - 1) : opts["startingSlide"] - 1;
            } else {
                opts["nextSlide"] = opts["startingSlide"] >= (els["length"] - 1) ? 0 : opts["startingSlide"] + 1;
            };
        };
        if (!opts["multiFx"]) {
            var init = $["fn"]["cycle"]["transitions"][opts["fx"]];
            if ($["isFunction"](init)) {
                init($cont, $slides, opts);
            } else {
                if (opts["fx"] != "custom" && !opts["multiFx"]) {
                    log("unknown transition: " + opts["fx"], "; slideshow terminating");
                    return false;
                };
            };
        };
        var e0 = $slides[lIl1];
        if (opts["before"]["length"]) {
            opts["before"][0]["apply"](e0, [e0, e0, opts, true]);
        };
        if (opts["after"]["length"] > 1) {
            opts["after"][1]["apply"](e0, [e0, e0, opts, true]);
        };
        if (opts["next"]) {
            $(opts["next"])["bind"](opts["prevNextEvent"], function() {
                return advance(opts, 1);
            });
        };
        if (opts["prev"]) {
            $(opts["prev"])["bind"](opts["prevNextEvent"], function() {
                return advance(opts, 0);
            });
        };
        if (opts["pager"] || opts["pagerAnchorBuilder"]) {
            buildPager(els, opts);
        };
        exposeAddSlide(opts, els);
        return opts;
    };

    function saveOriginalOpts(opts) {
        opts["original"] = {
            before: [],
            after: []
        };
        opts["original"]["cssBefore"] = $["extend"]({}, opts["cssBefore"]);
        opts["original"]["cssAfter"] = $["extend"]({}, opts["cssAfter"]);
        opts["original"]["animIn"] = $["extend"]({}, opts["animIn"]);
        opts["original"]["animOut"] = $["extend"]({}, opts["animOut"]);
        $["each"](opts["before"], function() {
            opts["original"]["before"]["push"](this);
        });
        $["each"](opts["after"], function() {
            opts["original"]["after"]["push"](this);
        });
    };

    function supportMultiTransitions(opts) {
        var i, tx, txs = $["fn"]["cycle"]["transitions"];
        if (opts["fx"]["indexOf"](",") > 0) {
            opts["multiFx"] = true;
            opts["fxs"] = opts["fx"]["replace"](/\s*/g, "")["split"](",");
            for (i = 0; i < opts["fxs"]["length"]; i++) {
                var fx = opts["fxs"][i];
                tx = txs[fx];
                if (!tx || !txs["hasOwnProperty"](fx) || !$["isFunction"](tx)) {
                    log("discarding unknown transition: ", fx);
                    opts["fxs"]["splice"](i, 1);
                    i--
                }
            };
            if (!opts["fxs"]["length"]) {
                log("No valid transitions named; slideshow terminating.");
                return false
            }
        } else {
            if (opts["fx"] == "all") {
                opts["multiFx"] = true;
                opts["fxs"] = [];
                for (p in txs) {
                    tx = txs[p];
                    if (txs["hasOwnProperty"](p) && $["isFunction"](tx)) {
                        opts["fxs"]["push"](p)
                    }
                }
            }
        };
        if (opts["multiFx"] && opts["randomizeEffects"]) {
            var l1l11 = Math["floor"](Math["random"]() * 20) + 30;
            for (i = 0; i < l1l11; i++) {
                var l1l1I = Math["floor"](Math["random"]() * opts["fxs"]["length"]);
                opts["fxs"]["push"](opts["fxs"]["splice"](l1l1I, 1)[0])
            };
            debug("randomized fx sequence: ", opts["fxs"])
        };
        return true
    };

    function exposeAddSlide(opts, els) {
        opts["addSlide"] = function(l1lIl, prepend) {
            var l1lI1 = $(l1lIl),
                s = l1lI1[0];
            if (!opts["autostopCount"]) {
                opts["countdown"]++
            };
            els[prepend ? "unshift" : "push"](s);
            if (opts["els"]) {
                opts["els"][prepend ? "unshift" : "push"](s)
            };
            opts["slideCount"] = els["length"];
            l1lI1["css"]("position", "absolute");
            l1lI1[prepend ? "prependTo" : "appendTo"](opts.$cont);
            if (prepend) {
                opts["currSlide"]++;
                opts["nextSlide"]++
            };
            if (!$["support"]["opacity"] && opts["cleartype"] && !opts["cleartypeNoBg"]) {
                clearTypeFix(l1lI1)
            };
            if (opts["fit"] && opts["width"]) {
                l1lI1["width"](opts["width"])
            };
            if (opts["fit"] && opts["height"] && opts["height"] != "auto") {
                l1lI1["height"](opts["height"])
            };
            s["cycleH"] = (opts["fit"] && opts["height"]) ? opts["height"] : l1lI1["height"]();
            s["cycleW"] = (opts["fit"] && opts["width"]) ? opts["width"] : l1lI1["width"]();
            l1lI1["css"](opts["cssBefore"]);
            if (opts["pager"] || opts["pagerAnchorBuilder"]) {
                $["fn"]["cycle"]["createPagerAnchor"](els["length"] - 1, s, $(opts["pager"]), els, opts)
            };
            if ($["isFunction"](opts["onAddSlide"])) {
                opts["onAddSlide"](l1lI1)
            } else {
                l1lI1["hide"]()
            }
        }
    };
    $["fn"]["cycle"]["resetState"] = function(opts, fx) {
        fx = fx || opts["fx"];
        opts["before"] = [];
        opts["after"] = [];
        opts["cssBefore"] = $["extend"]({}, opts["original"]["cssBefore"]);
        opts["cssAfter"] = $["extend"]({}, opts["original"]["cssAfter"]);
        opts["animIn"] = $["extend"]({}, opts["original"]["animIn"]);
        opts["animOut"] = $["extend"]({}, opts["original"]["animOut"]);
        opts["fxFn"] = null;
        $["each"](opts["original"]["before"], function() {
            opts["before"]["push"](this)
        });
        $["each"](opts["original"]["after"], function() {
            opts["after"]["push"](this)
        });
        var init = $["fn"]["cycle"]["transitions"][fx];
        if ($["isFunction"](init)) {
            init(opts.$cont, $(opts["elements"]), opts)
        }
    };

    function go(els, opts, manual, fwd) {
        if (manual && opts["busy"] && opts["manualTrump"]) {
            debug("manualTrump in go(), stopping active transition");
            $(els)["stop"](true, true);
            opts["busy"] = false
        };
        if (opts["busy"]) {
            debug("transition active, ignoring new tx request");
            return
        };
        var p = opts["$cont"][0],
            curr = els[opts["currSlide"]],
            next = els[opts["nextSlide"]];
        if (p["cycleStop"] != opts["stopCount"] || p["cycleTimeout"] === 0 && !manual) {
            return
        };
        if (!manual && !p["cyclePause"] && !opts["bounce"] && ((opts["autostop"] && (--opts["countdown"] <= 0)) || (opts["nowrap"] && !opts["random"] && opts["nextSlide"] < opts["currSlide"]))) {
            if (opts["end"]) {
                opts["end"](opts)
            };
            return
        };
        var l1lII = false;
        if ((manual || !p["cyclePause"]) && (opts["nextSlide"] != opts["currSlide"])) {
            l1lII = true;
            var fx = opts["fx"];
            curr["cycleH"] = curr["cycleH"] || $(curr)["height"]();
            curr["cycleW"] = curr["cycleW"] || $(curr)["width"]();
            next["cycleH"] = next["cycleH"] || $(next)["height"]();
            next["cycleW"] = next["cycleW"] || $(next)["width"]();
            if (opts["multiFx"]) {
                if (opts["lastFx"] == undefined || ++opts["lastFx"] >= opts["fxs"]["length"]) {
                    opts["lastFx"] = 0
                };
                fx = opts["fxs"][opts["lastFx"]];
                opts["currFx"] = fx
            };
            if (opts["oneTimeFx"]) {
                fx = opts["oneTimeFx"];
                opts["oneTimeFx"] = null
            };
            $["fn"]["cycle"]["resetState"](opts, fx);
            if (opts["before"]["length"]) {
                $["each"](opts["before"], function(i, o) {
                    if (p["cycleStop"] != opts["stopCount"]) {
                        return
                    };
                    o["apply"](next, [curr, next, opts, fwd])
                })
            };
            var after = function() {
                $["each"](opts["after"], function(i, o) {
                    if (p["cycleStop"] != opts["stopCount"]) {
                        return
                    };
                    o["apply"](next, [curr, next, opts, fwd])
                })
            };
            debug("tx firing; currSlide: " + opts["currSlide"] + "; nextSlide: " + opts["nextSlide"]);
            opts["busy"] = 1;
            if (opts["fxFn"]) {
                opts["fxFn"](curr, next, opts, after, fwd, manual && opts["fastOnEvent"])
            } else {
                if ($["isFunction"]($["fn"]["cycle"][opts["fx"]])) {
                    $["fn"]["cycle"][opts["fx"]](curr, next, opts, after, fwd, manual && opts["fastOnEvent"])
                } else {
                    $["fn"]["cycle"]["custom"](curr, next, opts, after, fwd, manual && opts["fastOnEvent"])
                }
            }
        };
        if (l1lII || opts["nextSlide"] == opts["currSlide"]) {
            opts["lastSlide"] = opts["currSlide"];
            if (opts["random"]) {
                opts["currSlide"] = opts["nextSlide"];
                if (++opts["randomIndex"] == els["length"]) {
                    opts["randomIndex"] = 0
                };
                opts["nextSlide"] = opts["randomMap"][opts["randomIndex"]];
                if (opts["nextSlide"] == opts["currSlide"]) {
                    opts["nextSlide"] = (opts["currSlide"] == opts["slideCount"] - 1) ? 0 : opts["currSlide"] + 1
                }
            } else {
                if (opts["backwards"]) {
                    var l11ll = (opts["nextSlide"] - 1) < 0;
                    if (l11ll && opts["bounce"]) {
                        opts["backwards"] = !opts["backwards"];
                        opts["nextSlide"] = 1;
                        opts["currSlide"] = 0
                    } else {
                        opts["nextSlide"] = l11ll ? (els["length"] - 1) : opts["nextSlide"] - 1;
                        opts["currSlide"] = l11ll ? 0 : opts["nextSlide"] + 1
                    }
                } else {
                    var l11ll = (opts["nextSlide"] + 1) == els["length"];
                    if (l11ll && opts["bounce"]) {
                        opts["backwards"] = !opts["backwards"];
                        opts["nextSlide"] = els["length"] - 2;
                        opts["currSlide"] = els["length"] - 1
                    } else {
                        opts["nextSlide"] = l11ll ? 0 : opts["nextSlide"] + 1;
                        opts["currSlide"] = l11ll ? els["length"] - 1 : opts["nextSlide"] - 1
                    }
                }
            }
        };
        if (l1lII && opts["pager"]) {
            opts["updateActivePagerLink"](opts["pager"], opts["currSlide"], opts["activePagerClass"])
        };
        var ms = 0;
        if (opts["timeout"] && !opts["continuous"]) {
            ms = getTimeout(els[opts["currSlide"]], els[opts["nextSlide"]], opts, fwd)
        } else {
            if (opts["continuous"] && p["cyclePause"]) {
                ms = 10
            }
        };
        if (ms > 0) {
            p["cycleTimeout"] = setTimeout(function() {
                go(els, opts, 0, !opts["backwards"])
            }, ms)
        }
    };
    $["fn"]["cycle"]["updateActivePagerLink"] = function(pager, currSlide, clsName) {
        $(pager)["each"](function() {
            $(this)["children"]()["removeClass"](clsName)["eq"](currSlide)["addClass"](clsName)
        })
    };

    function getTimeout(curr, next, opts, fwd) {
        if (opts["timeoutFn"]) {
            var t = opts["timeoutFn"]["call"](curr, curr, next, opts, fwd);
            while ((t - opts["speed"]) < 250) {
                t += opts["speed"]
            };
            debug("calculated timeout: " + t + "; speed: " + opts["speed"]);
            if (t !== false) {
                return t
            }
        };
        return opts["timeout"]
    };
    $["fn"]["cycle"]["next"] = function(opts) {
        advance(opts, 1)
    };
    $["fn"]["cycle"]["prev"] = function(opts) {
        advance(opts, 0)
    };

    function advance(opts, moveForward) {
        var l11l1 = moveForward ? 1 : -1;
        var els = opts["elements"];
        var p = opts["$cont"][0],
            timeout = p["cycleTimeout"];
        if (timeout) {
            clearTimeout(timeout);
            p["cycleTimeout"] = 0
        };
        if (opts["random"] && l11l1 < 0) {
            opts["randomIndex"]--;
            if (--opts["randomIndex"] == -2) {
                opts["randomIndex"] = els["length"] - 2
            } else {
                if (opts["randomIndex"] == -1) {
                    opts["randomIndex"] = els["length"] - 1
                }
            };
            opts["nextSlide"] = opts["randomMap"][opts["randomIndex"]]
        } else {
            if (opts["random"]) {
                opts["nextSlide"] = opts["randomMap"][opts["randomIndex"]]
            } else {
                opts["nextSlide"] = opts["currSlide"] + l11l1;
                if (opts["nextSlide"] < 0) {
                    if (opts["nowrap"]) {
                        return false
                    };
                    opts["nextSlide"] = els["length"] - 1
                } else {
                    if (opts["nextSlide"] >= els["length"]) {
                        if (opts["nowrap"]) {
                            return false
                        };
                        opts["nextSlide"] = 0
                    }
                }
            }
        };
        var cb = opts["onPrevNextEvent"] || opts["prevNextClick"];
        if ($["isFunction"](cb)) {
            cb(l11l1 > 0, opts["nextSlide"], els[opts["nextSlide"]])
        };
        go(els, opts, 1, moveForward);
        return false
    };

    function buildPager(els, opts) {
        var $p = $(opts["pager"]);
        $["each"](els, function(i, o) {
            $["fn"]["cycle"]["createPagerAnchor"](i, o, $p, els, opts)
        });
        opts["updateActivePagerLink"](opts["pager"], opts["startingSlide"], opts["activePagerClass"])
    };
    $["fn"]["cycle"]["createPagerAnchor"] = function(i, el, $p, els, opts) {
        var a;
        if ($["isFunction"](opts["pagerAnchorBuilder"])) {
            a = opts["pagerAnchorBuilder"](i, el);
            debug("pagerAnchorBuilder(" + i + ", el) returned: " + a)
        } else {
            a = "<a href=\"#\">" + (i + 1) + "</a>"
        };
        if (!a) {
            return
        };
        var l11lI = $(a);
        if (l11lI["parents"]("body")["length"] === 0) {
            var l111l = [];
            if ($p["length"] > 1) {
                $p["each"](function() {
                    var l1111 = l11lI["clone"](true);
                    $(this)["append"](l1111);
                    l111l["push"](l1111[0])
                });
                l11lI = $(l111l)
            } else {
                l11lI["appendTo"]($p)
            }
        };
        opts["pagerAnchors"] = opts["pagerAnchors"] || [];
        opts["pagerAnchors"]["push"](l11lI);
        l11lI["bind"](opts["pagerEvent"], function(e) {
            e["preventDefault"]();
            opts["nextSlide"] = i;
            var p = opts["$cont"][0],
                timeout = p["cycleTimeout"];
            if (timeout) {
                clearTimeout(timeout);
                p["cycleTimeout"] = 0
            };
            var cb = opts["onPagerEvent"] || opts["pagerClick"];
            if ($["isFunction"](cb)) {
                cb(opts["nextSlide"], els[opts["nextSlide"]])
            };
            go(els, opts, 1, opts["currSlide"] < i)
        });
        if (!/^click/ ["test"](opts["pagerEvent"]) && !opts["allowPagerClickBubble"]) {
            l11lI["bind"]("click.cycle", function() {
                return false
            })
        };
        if (opts["pauseOnPagerHover"]) {
            l11lI["hover"](function() {
                opts["$cont"][0]["cyclePause"]++
            }, function() {
                opts["$cont"][0]["cyclePause"]--
            })
        }
    };
    $["fn"]["cycle"]["hopsFromLast"] = function(opts, fwd) {
        var l111I, l = opts["lastSlide"],
            c = opts["currSlide"];
        if (fwd) {
            l111I = c > l ? c - l : opts["slideCount"] - l
        } else {
            l111I = c < l ? l - c : l + opts["slideCount"] - c
        };
        return l111I
    };

    function clearTypeFix($slides) {
        debug("applying clearType background-color hack");

        function hex(s) {
            s = parseInt(s).toString(16);
            return s["length"] < 2 ? "0" + s : s
        };

        function getBg(e) {
            for (; e && e["nodeName"]["toLowerCase"]() != "html"; e = e["parentNode"]) {
                var l11Il = $["css"](e, "background-color");
                if (l11Il["indexOf"]("rgb") >= 0) {
                    var l11I1 = l11Il["match"](/\d+/g);
                    return "#" + hex(l11I1[0]) + hex(l11I1[1]) + hex(l11I1[2])
                };
                if (l11Il && l11Il != "transparent") {
                    return l11Il
                }
            };
            return "#ffffff"
        };
        $slides["each"](function() {
            $(this)["css"]("background-color", getBg(this))
        })
    };
    $["fn"]["cycle"]["commonReset"] = function(curr, next, opts, w, h, rev) {
        $(opts["elements"])["not"](curr)["hide"]();
        opts["cssBefore"]["opacity"] = 1;
        opts["cssBefore"]["display"] = "block";
        if (opts["slideResize"] && w !== false && next["cycleW"] > 0) {
            opts["cssBefore"]["width"] = next["cycleW"]
        };
        if (opts["slideResize"] && h !== false && next["cycleH"] > 0) {
            opts["cssBefore"]["height"] = next["cycleH"]
        };
        opts["cssAfter"] = opts["cssAfter"] || {};
        opts["cssAfter"]["display"] = "none";
        $(curr)["css"]("zIndex", opts["slideCount"] + (rev === true ? 1 : 0));
        $(next)["css"]("zIndex", opts["slideCount"] + (rev === true ? 0 : 1))
    };
    $["fn"]["cycle"]["custom"] = function(curr, next, opts, cb, fwd, speedOverride) {
        var l11II = $(curr),
            $n = $(next);
        var speedIn = opts["speedIn"],
            speedOut = opts["speedOut"],
            easeIn = opts["easeIn"],
            easeOut = opts["easeOut"];
        $n["css"](opts["cssBefore"]);
        if (speedOverride) {
            if (typeof speedOverride == "number") {
                speedIn = speedOut = speedOverride
            } else {
                speedIn = speedOut = 1
            };
            easeIn = easeOut = null
        };
        var fn = function() {
            $n["animate"](opts["animIn"], speedIn, easeIn, cb)
        };
        l11II["animate"](opts["animOut"], speedOut, easeOut, function() {
            if (opts["cssAfter"]) {
                l11II["css"](opts["cssAfter"])
            };
            if (!opts["sync"]) {
                fn()
            }
        });
        if (opts["sync"]) {
            fn()
        }
    };
    $["fn"]["cycle"]["transitions"] = {
        fade: function($cont, $slides, opts) {
            $slides["not"](":eq(" + opts["currSlide"] + ")")["css"]("opacity", 0);
            opts["before"]["push"](function(curr, next, opts) {
                $["fn"]["cycle"]["commonReset"](curr, next, opts);
                opts["cssBefore"]["opacity"] = 0
            });
            opts["animIn"] = {
                opacity: 1
            };
            opts["animOut"] = {
                opacity: 0
            };
            opts["cssBefore"] = {
                top: 0,
                left: 0
            }
        }
    };
    $["fn"]["cycle"]["ver"] = function() {
        return l1I1
    };
