function stripTags(s, n) {
    return s.replace(/<.*?>/ig, "").split(/\s+/).slice(0, n - 1).join(" ")
}

function rm(a) {
    var p = document.getElementById(a);
    imgtag = "";
    ifrtag = "";
    ifrsrc = "";
    ifrtb = -1;
    img = p.getElementsByTagName("img");
    ifr = p.getElementsByTagName("iframe");
    for (var i = 0; i < ifr.length; i++) {
        ifrsrc = ifr[i].src;
        if (ifrsrc.indexOf("//www.youtube.com/embed/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//player.vimeo.com/video/") != -1) {
            ifrtb = i;
            break
        } else if (ifrsrc.indexOf("//www.dailymotion.com/embed/video/") != -1) {
            ifrtb = i;
            break
        }
    }
    if (ifrtb != -1) ifrtag = '<div class="entry-video"><iframe width="750" height="370" src="' + ifrsrc + '?vq=medium&rel=0" frameborder="0" allowfullscreen></iframe></div>';
    else if (img.length == 1) imgtag = '<div class="entry-blog"><header class="page-header1"><span class="author">By<a href="' + y + '">' + z + '</a> </span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="publish-date">Posted on ' + t + '</li></br><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul><div class="#mygallery"><a href="' + y + '"><img width="750" height="270"  class="thumb" src="' + img[0].src + '" /></a></div>';
    else if (img.length > 1 && img.length <= 5) {
        var li = '';
        for (var i = 0; i < img.length; i++) {
            var li = li + '<a class="gal4" href="' + img[i].src + '"><img src="' + img[i].src + '"></a>'
        }
        imgtag = '<div class="entry-blog"><header class="page-header1"><span class="author">By<a href="' + y + '">' + z + '</a> </span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="publish-date">Posted on ' + t + '</li></br><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul><div class="mygal">' + li + '</div>'
    } else if (img.length > 5) {
        var li = '';
        for (var i = 0; i < img.length; i++) {
            var li = li + '<a class="gal" href="' + img[i].src + '"><img src="' + img[i].src + '"></a>'
        }
        imgtag = '<div class="entry-blog"><header class="page-header1"><span class="author">By<a href="' + y + '">' + z + '</a> </span><h2 class="page-title"><a href="' + y + '">' + x + '</a></h2><ul class="entry-meta"><li class="publish-date">Posted on ' + t + '</li></br><li class="comments-link"><a href="' + y + '#comments">' + u + ' Comments</a> </li></ul><div class="fotorama" data-loop="true"">' + li + '</div>'
    } else imgtag = '';
    p.innerHTML = ifrtag + imgtag + '<div class="entry-content cf">' + stripTags(p.innerHTML, 45) + '<span class="fixed-char"><a class="read-more" href="' + y + '">Continue Reading ></a></span></div>'
}
