    c = b.name.$t;
    d = b.gd$image.src.replace(/\/s[0-9]+(-*c*)\//, "/s55$1/");
    document.write('<img alt="' + c + '" class="avatar-author" src="' + d + '" title="' + c + '"/>')
};
